import React, { useEffect, useState,useContext, Fragment } from "react";
import proyectoContext from '../useContext/proyectoContext' ;
import Error from './Error' ;
import {Link} from 'react-router-dom' ;
import axios from "axios";
import MostrarCategoria from './MostrarCategoria' ;


const Genero = (props) => {

    

    const proyectosContext = useContext(proyectoContext) ;
    const {error,guardarError,stateBuscarCategorias,buscarCategorias,verCategorias}=  proyectosContext 
    
    const [categoria, guardarCategoria] = useState('')
    



    const onChange = e =>{
        guardarCategoria(e.target.value)
    }

    const mostrarCategoria =() =>{
        verCategorias(true)
    }

    //Cuando el usuario
    const onSubmit = e =>{
        e.preventDefault()

    if(categoria.trim()===''){
        return guardarError(true)
        
    }
}

    

    return ( 
        <Fragment>

        <form onSubmit={onSubmit}>
            <div className='campo-form'> 
                            <label htmlFor="categoria"> Ingresar nombre de la nueva Categoria</label>
                            <input 
                                type="text"
                                placeholder='Ingresar Categoria'
                                id='categoria'   
                                name='nombreCategoria' 
                                value={categoria}
                                onChange={onChange}
                            />
                            
                        </div>
            <button type='submit'>
                Enviar Informaci√≥n
            </button>
        </form>
            
        <button
                className='button'
                onSubmit={mostrarCategoria}
                type='button'
            > Ver todas las categorias existentes</button>
            {verCategorias ? <MostrarCategoria />  :null}
        
    </Fragment>



        
    );
    
}

export default Genero ;
