import React,{useContext,Fragment, useState, useEffect} from 'react' ;
import proyectoContext from '../useContext/proyectoContext' ;
import axios from 'axios' ;

const MostrarCategoria = () => {

    //useContext
    const proyectosContext = useContext(proyectoContext) ;
    const {error,guardarError,buscarCategorias,categoria}=  proyectosContext 

    //State local
    const [cambiarCategoria, guardarCambiarCategoria] = useState(false) ;
    const [nuevoNombreCategoria, guardarNuevoNombreCategoria] = useState({nombre:''}) ;
    const [eliminarCategoria, guardarEliminarCategoria] = useState(false) ;
    const [categoriaID, guardarCategoriaID] = useState(66)

    const {nombre} =nuevoNombreCategoria;



    const funcionEliminarCategoria = (categoriaID) =>{
        
        guardarCategoriaID(categoriaID)
        guardarEliminarCategoria(true)  

    } 


    useEffect(() => {
        const eliminarCategoria = () => {
            const url =`http://localhost:3001/categoria/${categoriaID}` ;
            axios.delete(url)
        
        .then (respuesta => {
            console.log(respuesta)
            guardarEliminarCategoria(false)
            
        })
        .catch(error => {
            console.log(error)
            
        })
    }
    eliminarCategoria()
    }, [eliminarCategoria]) 




    return ( 
        <Fragment>
        <div className='row justify-content-center'>
        
            <tr>
            <div className='col-12 p-5 row'>
                <div className='card'>
            {buscarCategorias.map(categorias => (
                <div className='col-12 col-sm-6 col-md-4 col-lg-3 mb-4'>
                    <div className='card'>
                <tr key={categorias.id}> 
                    <td>{categorias.nombre}</td> 
                    
                    <td>
                        <button
                            className='boton-categoria'
                            type='submit'
                            onClick={ e =>funcionEliminarCategoria (categorias.id)}
                        >Eliminar</button>
                    </td>
                    
                   
                </tr>   
                </div>
                </div> 
            ))}      
          </div>
          </div>
        </tr>
        
         </div>
    </Fragment>
    );
}

export default MostrarCategoria;
