import React, { useEffect, useState,Fragment } from "react";
import axios from "axios";


const Genero = (props) => {

    const [usuarios, stateUsuarios] = useState(null);

    useEffect(() => {
        const consultarApi = () => {
            axios.get("http://localhost:3001/categoria")
            .then(respuesta => {
                
                stateUsuarios(respuesta.data)
            })
            .catch(error => {
                console.log(error)
            })

        }
        consultarApi();

    }, []);
    
    const [categoria, guardarCategoria] = useState('')
    const onChange = e =>{
        guardarCategoria(e.target.value)
    }

    const onSubmit = e =>{
        e.preventDefault()

    if(categoria.trim()===''){
        console.log('error')
    }
    
    


    }


    return ( 
        <Fragment>
        <form onSubmit={onSubmit}>
            <div className='campo-form'> 
                            <label htmlFor="categoria"> Ingresar Categoria de la nueva categoria</label>
                            <input 
                                type="text"
                                placeholder='Ingresar Categoria'
                                id='categoria'   
                                name='nombreCategoria' 
                                value={categoria}
                                onChange={onChange}
                            />
                            
                        </div>
            <button type='submit'>
                Enviar Informaci√≥n
            </button>
        </form>

        <table>
            <thead>
                <tr>
                    <th>Nombre</th>
                    <th>Apellido</th>
                    <th>Alias</th>
                    <th>Email</th>
                </tr>
            </thead>
            <tbody>
                       
                            <tr>
                                {usuarios.map((usuario) => (
                                    <td>{usuario.nombre}</td>,
                                    <td>{usuario.apellido}</td>,
                                    <td>{usuario.alias}</td>,
                                    <td>{usuario.imail}</td>

                                ))}
                                <td>
                                    <button>Editar</button>
                                    <button>Eliminar</button>
                                </td>
                            </tr>
                
                   
            </tbody>
        </table>
    </Fragment>



        
    );
}

export default Genero;
