import React, { useEffect, useState,useContext, Fragment } from "react";
import proyectoContext from '../useContext/proyectoContext' ;
import Error from './Error' ;
import {Link} from 'react-router-dom'
import axios from "axios";


const Genero = (props) => {

    const [buscarCategorias, stateBuscarCategorias] = useState([{}]);

    //Conexion con la base de datos para consultar por las categorias
    useEffect(() => {
        const consultarApi = () => {
            axios.get("http://localhost:3001/categoria")
            .then(respuesta => {
                
                stateBuscarCategorias(respuesta.data.respuesta)
            })
            .catch(error => {
                console.log(error)
            })

        }
        consultarApi();

    }, []);
    console.log(nuevaCategoria)

    const proyectosContext = useContext(proyectoContext) ;
    const {error,guardarError}=  proyectosContext 
    
    const [categoria, guardarCategoria] = useState('')
    const [categorias, verCategorias] = useState(false)



    const onChange = e =>{
        guardarCategoria(e.target.value)
    }

    const onSubmit = e =>{
        e.preventDefault()

    if(categoria.trim()===''){
        return guardarError(true)
        
    }
    
    const verCategorias=() =>{
        verCategorias(true)
    }

    let componente;
    if(!categoria){
        <form onSubmit={onSubmit}>
            <div className='campo-form'> 
                            <label htmlFor="categoria"> Ingresar Categoria de la nueva categoria</label>
                            <input 
                                type="text"
                                placeholder='Ingresar Categoria'
                                id='categoria'   
                                name='nombreCategoria' 
                                value={categoria}
                                onChange={onChange}
                            />
                            
                        </div>
            <button type='submit'>
                Enviar Informaci√≥n
            </button>
        </form>
    }else{}

    }


    return ( 
        <Fragment>

            

            
        

        <table>
            <thead>
                <tr>
                    <th>Nombre</th>
                    <th>Apellido</th>
                    <th>Alias</th>
                    <th>Email</th>
                </tr>
            </thead>
            <tbody>
                       
                            <tr>
                                {buscarCategorias.map(categorias => (
                                    <td>{categorias.nombre}</td> 
                                    

                                 ))} 
                                <td>
                                    <button>Editar</button>
                                    <button>Eliminar</button>
                                </td>
                            </tr>
                
                   
            </tbody>
        </table>
        <button
                className='button'
                onSubmit={verCategorias}
                type='button'
            > Ver todas las categorias existentes</button>
        
    </Fragment>



        
    );
}

export default Genero;
