import React,{useContext,useEffect,useState} from 'react';
import proyectoContext from '../useContext/proyectoContext' ;
import axios from 'axios' ;



const MostrarLibrosCategorias = () => {

     //useContext
     const proyectosContext = useContext(proyectoContext) ;
     const {error,guardarError,buscarCategorias,mostrarLibros,categoriaID,guardarCategoriaID}=  proyectosContext 

    //State Local
    const [mostrarLibrosLista, guardarMostrarLibrosLista] = useState([{}])

    useEffect(() => {
        const MostrarLibrosCategoria = () =>{
            const url =`http://localhost:3001/libro/${categoriaID}` ;
            axios.get(url) 
            .then(respuesta => {
                guardarMostrarLibrosLista(respuesta.data)
                console.log(respuesta.data)


            })
            .catch(error => {
                console.log(error)
                
            })
        }
        MostrarLibrosCategoria()
    }, [mostrarLibros])

    
    return ( <h1>asd</h1> );
}
 
export default MostrarLibrosCategorias;
