import React,{useState} from 'react'

import proyectoContext from './proyectoContext'

const ProyectoState =   props =>{

    const [error, guardarError] = useState(false)
    const [usuarios, guardarUsuario] =useState(
        [{}]
    )

    
    const agregarUsuario = usuario => (
        guardarUsuario( [...usuarios,  usuario])
    )
    
    
    
    const [categoria, stateCategoria] = useState([{}]);
    const [mostrarCategorias, guardarMostrarCategorias] = useState(false)

    //Conexion con la base de datos para consultar por las categorias
    useEffect(() => {
        const consultarApi = () => {
            axios.get("http://localhost:3001/categoria")
            .then(respuesta => {
                
                stateCategoria(respuesta.data.respuesta)
            })
            .catch(error => {
                console.log(error)
            })

        }
        consultarApi();
        mostrarCategorias(false)

    }, [mostrarCategorias]);
    console.log(categoria)
    
    


    return(
        <proyectoContext.Provider 
            value={{
                error,
                guardarError,
                agregarUsuario,
                usuarios,
                categoria,
                stateCategoria,
                guardarMostrarCategorias
            }}
        >

            {props.children}

        </proyectoContext.Provider>
    )
}


export default ProyectoState ;
