import React, { useEffect, useState,useContext, Fragment } from "react";
import proyectoContext from '../useContext/proyectoContext' ;
import Error from './Error' ;
import {Link} from 'react-router-dom'
import axios from "axios";


const Genero = (props) => {

    

    const proyectosContext = useContext(proyectoContext) ;
    const {error,guardarError,stateBuscarCategorias,buscarCategorias,verCategorias}=  proyectosContext 
    
    const [categoria, guardarCategoria] = useState('')
    



    const onChange = e =>{
        guardarCategoria(e.target.value)
    }

    const onSubmit = e =>{
        e.preventDefault()

    if(categoria.trim()===''){
        return guardarError(true)
        
    }
    
    const mostrarCategoria=() =>{
        verCategorias(true)
    }

    let componente;
    if(!categoria){
        <form onSubmit={onSubmit}>
            <div className='campo-form'> 
                            <label htmlFor="categoria"> Ingresar Categoria de la nueva categoria</label>
                            <input 
                                type="text"
                                placeholder='Ingresar Categoria'
                                id='categoria'   
                                name='nombreCategoria' 
                                value={categoria}
                                onChange={onChange}
                            />
                            
                        </div>
            <button type='submit'>
                Enviar Informaci√≥n
            </button>
        </form>
    }else{}

    }


    return ( 
        <Fragment>

            

            
        

        <table>
            <thead>
                <tr>
                    <th>Nombre</th>
                    <th>Apellido</th>
                    <th>Alias</th>
                    <th>Email</th>
                </tr>
            </thead>
            <tbody>
                       
                            <tr>
                                {buscarCategorias.map(categorias => (
                                    <td>{categorias.nombre}</td> 
                                    

                                 ))} 
                                <td>
                                    <button>Editar</button>
                                    <button>Eliminar</button>
                                </td>
                            </tr>
                
                   
            </tbody>
        </table>
        <button
                className='button'
                onSubmit={mostrarCategorias}
                type='button'
            > Ver todas las categorias existentes</button>
        
    </Fragment>



        
    );
}

export default Genero;
